// src/main.js

import { createApp } from 'vue';
import App from './App.vue';
import router from './router'; // Import the router
import './styles/custom-bulma.scss'; // Import the custom Bulma file

createApp(App)
  .use(router)  // Use the router
  .mount('#app');  // Mount the app
