// src/router/index.js

import { createRouter, createWebHistory } from 'vue-router';
import Home from '../views/HomePage.vue';  // Ensure the paths are correct
import About from '../views/AboutPage.vue'; // Ensure the paths are correct

const routes = [
  {
    path: '/',
    name: 'HomePage',
    component: HomePage, // Reference the correct component
  },
  {
    path: '/about',
    name: 'AboutPage',
    component: AboutPage, // Reference the correct component
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
