// src/router/index.js

import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '../views/HomePage.vue';  // Ensure the paths are correct
import AboutPage from '../views/AboutPage.vue'; // Ensure the paths are correct
import HospitalForm from '../views/HospitalForm.vue'; // Import HospitalForm component
import ConfirmForm from '../views/ConfirmForm.vue';
import HospitalList from '@/views/HospitalList.vue';
import EditForm from '../views/EditForm.vue';
import RidesToday from '../views/RidesToday.vue';
import AdminDashboard from '@/views/AdminDashboard.vue';
import BusSchedules from '@/views/BusSchedules.vue';
import TaxiUsers from '@/views/TaxiUsers.vue';
import RegisteredRides from '@/views/RegisteredRides.vue';
import DriverView from '@/views/DriverView.vue';
import TranslatorView from '@/views/TranslatorView.vue';


const routes = [
  {
    path: '/',
    name: 'HomePage',
    component: HomePage, // Reference the correct component
  },
  {
    path: '/hospitals',
    name: 'HospitalList',
    component: HospitalList,
  },
  {
    path: '/hospital/:id', // Dynamic route for HospitalForm with :id as a parameter
    name: 'HospitalForm',
    component: HospitalForm,
    props: true, // Pass the route parameters as props to the component
  },
  {
  path: '/confirm',
  name: 'ConfirmForm',
  component: ConfirmForm,
  props: route => ({
    formData: route.params.formData,
    busTime: route.params.busTime
  })
  },
  {
    path: '/rides-today',
    name: 'RidesToday',
    component: RidesToday,
  },
  {
    path: '/edit/:userId',
    name: 'EditForm',
    component: EditForm,
    props: true  // Pass the userId as a prop to EditForm
  },
  {
    path: '/about',
    name: 'AboutPage',
    component: AboutPage, // Reference the correct component
  },
  {
    path: '/dashboard',
    name: 'AdminDashboard',
    component: AdminDashboard,
    meta: { requiresStaff: true },  // Mark this route as restricted

  },
  {
    path: '/rides-today',
    name: 'RidesToday',
    component: RidesToday
  },
  {
    path: '/bus-schedules',
    name: 'BusSchedules',
    component: BusSchedules
  },
  {
    path: '/taxi-users',
    name: 'TaxiUsers',
    component: TaxiUsers
  },
  {
    path: '/translators',
    name: 'TranslatorView',
    component: TranslatorView
  },
  {
    path: '/registered-rides',
    name: 'RegisteredRides',
    component: RegisteredRides
  },
  {
    path: '/driver-view',
    name: 'DriverView',
    component: DriverView
  }
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

router.beforeEach(async (to, from, next) => {
  if (to.matched.some(record => record.meta.requiresStaff)) {
    try {
      // Fetch user information from the backend (you may need to adjust the endpoint)
      const response = await axios.get('http://localhost:8000/api/userinfo/');
      const userInfo = response.data;

      if (userInfo.is_staff) {
        // Proceed to the restricted route if the user is a staff member
        next();
      } else {
        // Redirect non-staff members to the home page or show an error
        next({ name: 'HomePage' });
      }
    } catch (error) {
      // If there is an error (e.g., the user is not authenticated), redirect to login or home
      next({ name: 'HomePage' });
    }
  } else {
    // Proceed normally if the route does not require staff
    next();
  }
});

export default router;
