import { defineStore } from 'pinia';

export const useFormStore = defineStore('form', {
  state: () => ({
    formData: {
      name: '',
      room: '',
      appointment_date: '',
      appointment_time: '',
      department: '',
      needs_translator: false,
    },
    busTime: null,  
  }),
  actions: {
    setFormData(data) {
      this.formData = data; 
    },
    setBusTime(busTime) {
      this.busTime = busTime;
    },
  },
});
