// stores/auth.js
import { defineStore } from 'pinia';
import axios from 'axios';

export const useAuthStore = defineStore('auth', {
    state: () => ({
        token: localStorage.getItem('token') || null,
    }),
    getters: {
        isAuthenticated: (state) => !!state.token,  // Reactive getter based on token
    },
    actions: {
        async login(email, password) {
            try {
                const response = await axios.post(`${process.env.VUE_APP_BACKEND_URL}/api/token/`, {
                    email,
                    password,
                });

                this.token = response.data.access;
                localStorage.setItem('token', this.token);  // Store token in localStorage

                // Make sure token updates are reactive
                return true;
            } catch (error) {
                throw new Error('Login failed');
            }
        },
        logout() {
            this.token = null;
            localStorage.removeItem('token');  // Clear token from localStorage
        },
    },
});
