import { defineStore } from 'pinia';

export const useFormStore = defineStore('form', {
  state: () => ({
    formData: {
      name: '',
      room: '',
      appointment_date: '',
      appointment_time: '',
      department: '',
      needs_translator: false,
      busTime: null,  // This is now part of formData
    },
  }),
  actions: {
    setFormData(data) {
      this.formData = { ...this.formData, ...data }; // Merge the new data with the existing formData
    },
    setBusTime(busTime) {
      this.formData.busTime = busTime;
    },
  },
});
