import { defineStore } from 'pinia';
import axios from 'axios';

export const useAuthStore = defineStore('auth', {
    state: () => ({
        token: localStorage.getItem('token') || null,  // Initialize token from localStorage
    }),
    getters: {
        isAuthenticated: (state) => !!state.token,  // Check if the token exists
    },
    actions: {
        async login(email, password) {
            try {
                const response = await axios.post(`${process.env.VUE_APP_BACKEND_URL}/api/token/`, {
                    email,
                    password,
                });

                if (response && response.data && response.data.access) {
                    this.token = response.data.access;
                    localStorage.setItem('token', this.token);  // Store the token in localStorage
                } else {
                    throw new Error('Invalid login response');
                }
            } catch (error) {
                console.error('Login failed:', error.response ? error.response.data : error.message);
                throw new Error('Login failed. Please check your credentials.');
            }
        },
        logout() {
            try {
                this.token = null;
                localStorage.removeItem('token');  // Clear the token from localStorage
            } catch (error) {
                console.error('Error logging out:', error.message);
            }
        },
        // Ensure the token is restored on app start
        restoreToken() {
            try {
                this.token = localStorage.getItem('token');
            } catch (error) {
                console.error('Error restoring token:', error.message);
            }
        },
    },
});
